import React, { Component } from "react";
import Chart from "react-apexcharts";

class Grafico extends Component {
  constructor(props) {
    super(props);

    this.state = {
      options: {
        chart: {
          id: "basic-bar",
        },
        xaxis: {
          categories: [
            "HTML",
            "CSS",
            "JavaScript"
            
          ],
        colors: ['#546E7A', '#E91E63','#E91E63']
        },
      },
      series: [
        {
          name: "series-1",
          data: [20, 40, 45],
        },
      ],
    };
  }

  render() {
    return (
      <div className="app">
        <div className="row">
          <div className="mixed-chart">
            <Chart
              options={this.state.options}
              series={this.state.series}
              type="bar"
              width="700"
              background
            />
          </div>
        </div>
      </div>
    );
  }
}

export default Grafico;
