import React, { Component } from "react";
import Chart from "react-apexcharts";

class Grafico extends Component {
  constructor(props) {
    super(props);

    this.state = {
      options: {
        chart: {
          id: "basic-bar",
        },
        xaxis: {
          categories: [
            "HTML",
            "CSS",
            "JavaScript"
            
          ],
        },
      },
      series: [
        {
          name: "series-1",
          data: [30, 40, 45],
        },
      ],
      theme: {
        mode: 'light', 
        palette: 'palette1', 
        monochrome: {
            enabled: false,
            color: 'black',
            shadeTo: 'dark',
            shadeIntensity: 0.65
        },
    }
    };
  }

  render() {
    return (
      <div className="app">
        <div className="row">
          <div className="mixed-chart">
            <Chart
              options={this.state.options}
              series={this.state.series}
              theme={this.state.theme}
              type="bar"
              width="700"
            />
          </div>
        </div>
      </div>
    );
  }
}

export default Grafico;
