  background-color: #f1f1f1;
  min-height: 90vh;
  padding: 0px 15vw;

  @media (max-width: 800px) {
    flex-direction: column;
  }
`;

const Conteudo = styled.section`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;

export default () => {
  return (
