import React, { Component } from "react";
import {Chart, color} from "react-apexcharts";

class Grafico extends Component {
  constructor(props) {
    super(props);

    this.state = {
      options: {
        chart: {
          id: "basic-bar",
        },
        xaxis: {
           
          categories: ["HTML", "CSS", "JavaScript", "1994", "1995", "1996", "1997", "1998","1998"],
        },
      },
      series: [
        {
          
          name: "series-1",
          data: [30, 40, 45, 50, 49, 60, 70, 91, 30],
          colors: ["#2E93fA", "#66DA26", "#546E7A", "#E91E63", "#FF9800"]
        },
      ],
      theme: {
        mode: 'light', 
        palette: 'palette1', 
        monochrome: {
            enabled: false,
            color: '#255aee',
            shadeTo: 'light',
            shadeIntensity: 0.65
        },
    }
    };

  }

  render() {
    return (
      <div className="app">
        <div className="row">
          <div className="mixed-chart">
            <Chart
              options={this.state.options}
              series={this.state.series}
              type="bar"
              width="700"
            />
          </div>
        </div>
      </div>
    );
  }
}

export default Grafico;
