import React, { useState } from "react";
import styled from "styled-components";

import privado from "../../assets/images/privado.svg";
import olho_icone from "../../assets/images/olho.svg";
import dinheiro from "../../assets/images/dinheiro.svg";

import {Icone, Box, Botao, Saldo, Detalhe, IconeTema, Input} from "../../Components/ui/index"

const IconeMargin = styled(Icone)`
margin-top: 2px;
`

const Conta = () => {
  const [toggleState, untoggle] = useState(true);

  const [saldo, setSaldo] = useState(0);

  const [obterSaldo, setInput] = useState(0)

  const atualizaSaldo = () => {
    setSaldo(saldo);
  }




  const toggleHandler = () => {
    untoggle((toggleState) => !toggleState);
  };

  return (
    <Box>
      <h2>Conta</h2>
      <div style={{ fontSize: "26px", padding: "20px 0" }}>
        Saldo disponível{" "}
        <span>
          <IconeTema src={dinheiro} alt="Ícone Saldo" />
        </span>
        {toggleState ? (
          <Saldo>
            <Detalhe>R$</Detalhe> {saldo}{" "}
          </Saldo>
        ) : null}
        <Botao onClick={atualizaSaldo}>Atualizar</Botao>
        <Input onChange={getSaldo} type="number" />
      </div>

      <Botao onClick={toggleHandler}>
        <IconeMargin
          src={toggleState ? privado : olho_icone}
          alt="Privacidade do Saldo"
        />
      </Botao>
    </Box>
  );
};

export default Conta;
